long-dash.css:
.long-dash {
  font-size: 0;
}
.cm-active .long-dash {
  font-size: var(--font-text-size);
  color: rgba(251, 70, 76, 1) !important;
}
.long-dash::before {
  font-size: var(--font-text-size);
  content: "➖";
  color: rgba(209, 63, 141, 0.2) !important;
}
.cm-active .long-dash::before {
  font-size: 0;
}
